var Utils=require("./utils").Utils;var cf=require("mc-curseforge-api");function queryLatest(id){cf.getMod(id).then(function(mod){var latestFile=mod.latestFiles[mod.latestFiles.length-1];var embed=Utils.buildModEmbed(mod,latestFile);return embed},function(error){return"Error: "+error+"!"})}function getModById(id){var modInstance=null;cf.getMod(id).then(function(mod){modInstance=mod});return modInstance}exports.cfquery={queryLatest:queryLatest,getModById:getModById};
